import React, { useState, useEffect } from "react";
import { Container } from "react-bootstrap";

function Reloj({hora}) {
    return <h4>{hora}</h4>
}

const Home = ()=>{

    const [hora, setHora] = useState(new Date().toLocaleTimeString());

    useEffect(() => {
        setHora(new Date().toLocaleTimeString());
    })
              
    return (

    <Container  style={{bacgroundColor:"red",backgroundImage: "url(/img.jpg)",backgroundRepeat: 'no-repeat',
    width:'5550px', height:'5550'}}>
        <h1>HOmeee</h1>
       
        <p>En Home pondremos una presentación corporativa y como todos los demas componentes , tendrá su BarNav y su Footer</p>
        <h4 style={{color:"pink", diplay:"flex", justifyContent:"end"}}><Reloj hora={hora}/></h4>
    </Container>
   
    )
}
export default Home;
